export const pageAnimation = {
    hidden:{
        opacity: 0,
        y: 300
    },
    show: {
        opacity: 1,
        y: 0,
        transition: {
            duration: 1, 
        }
    },
    exit: {
        opacity: 0,
        y: 300,
        transition: {
            duration: 1,
            when: "beforeChildren"
        },
    },
};

export const titleAnim = {
    hidden: {y: 200}, 
    show: {
        y:0, transition: {
            duration: 0.75, ease: "easeOut"
        }
    }
}
