import {createGlobalStyles, withTheme} from 'styled-components'

const GlobalStyle = createGlobalStyles`

*{
    margin: 0;
    padding: 0;
    box-sizing: border-box
}

body{
    background: 1b1b1b;
}

button{
    font-weight: bold;
    font-size: 1.1 removeEventListener;
    cursor: pointer;
    padding: 1rem 2rem;
    border: 3px solid #23d997;
    background: transparent;
    color: withTheme;
    transition: all 0.5 ease;
    &:hover{
        background-color: #23d997;
        color: white;
    }
}
`;

export default GlobalStyle;
